mapper.select:
SELECT a.menuId, a.parentId, a.`name`, a.url, a.perms, a.type, a.icon, a.orderNum, a.createTime, a.modifiedTime FROM sys_menu AS a
and a.menuId = #{menuId} and a.parentId = #{parentId} and a.type = #{type}
order by ${sort} ${order}
order by a.menuId desc
limit #{offset}, #{limit}


		SELECT
			COUNT(*)
		FROM
			sys_menu AS a


  		 and a.menuId = #{menuId} 
		 and a.parentId = #{parentId} 
		 and a.type = #{type}
